package Whileloop;

public class Eighteen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=1;
		while(i<=10) {
             if(i%2!=0) {
             System.out.print(i+" ");
	}
             else {
            	 System.out.print(i-(i+i)+" ");
             }
             i++;
	}

}
}